mapper.insert:
		insert into members
		(
			members_no, members_id, members_full_name, members_nickname, members_pw, members_email, members_tel) values
		(
 			MEMBERS_NO_SEQ.nextval, #{members_id}, #{members_full_name}, #{members_nickname}, #{members_pw}, #{members_email}, #{members_tel}
 		)

mapper.select:
SELECT  members_no,members_id,members_full_name, members_nickname, members_pw, members_email,members_tel, member_isShow
FROM 	MEMBERS
WHERE 	members_no = #{members_no}
